import { Board } from '../../models/board';

import {Props} from './types';

import './board.css';
import CellComponent from '../cell/cell';

const cn = 'board';


const BoardComponent: Props = ({board, setBoard}) => {

    const boardFinal = useMemo(() => {
        return (
            <>
                {board.cells.map((row, index) => (
                    <div key={index}>
                        {row.map((cell, index) => (
                            <span key={index}>
                                <CellComponent/>
                            </span>
                        ))}
                    </div>
                ))}
            </>
        )
    }, []);

    return (
        <div className={cn}>

        </div>
    );
}

export default BoardComponent;
