import React, { useEffect, useMemo, useState, Fragment, useCallback} from 'react';
import { Board } from '../../models/board';

import {Props} from './types';

import './board.css';
import CellComponent from '../cell/cell';
import { Cell } from '../../models/cell';
import { Nullable } from '../../consts';

const cn = 'board';

const BoardComponent: Props = ({board, setBoard}) => {
    const [selectedCell, setSelectedCell] = useState<Nullable<Cell>>(null);

    useEffect(() => {

    }, [board])

    const clickCellHandler = useCallback((cell: Cell) => {
        setSelectedCell(cell.currentFigure ? cell : null);
        setBoard(board.updateBoard())
        board.highlightCells(cell);
    }, [board])

    const boardFinal = useMemo(() => {
        return (
            <>
                {board.cells.map((row, index) => (
                    <Fragment key={index}>
                        {row.map(cell => (
                            <div 
                                key={cell.id}
                                onClick={() => clickCellHandler(cell)}
                                className={`${cn}-cell`}
                            >
                                <CellComponent 
                                    cell={cell}
                                    isSelected={selectedCell?.x === cell.x && selectedCell.y === cell.y}
                                />
                            </div>
                        ))}
                    </Fragment>
                ))}
            </>
        )
    }, [board, selectedCell]);

    return (
        <div className={cn}>
            {boardFinal}
        </div>
    );
}

export default BoardComponent;
