import whiteLogo from '../../assets/white-queen.png';
import blackLogo from '../../assets/black-queen.png';

import { Colors, FigureNames } from "../../consts";
import { Cell } from "../cell";
import { Figure } from "../figure";
import { Rook } from './rook';
import { Bishop } from './bishop';

export class Queen extends Figure {
    constructor(color: Colors, cell: Cell) {
        super(color, cell);
        this.logo = this.color === Colors.WHITE ? whiteLogo : blackLogo;
        this.figureName = FigureNames.QUEEN;
    }

    public canMove(target: Cell): boolean {
        return new Rook(this.color, this.cell).canMove(target) || new Bishop(this.color, this.cell).canMove(target);
    }
}
