import whiteLogo from '../../assets/white-knight.png';
import blackLogo from '../../assets/black-knight.png';

import { Colors, FigureNames } from "../../consts";
import { Cell } from "../cell";
import { Figure } from "../figure";

export class Knight extends Figure {
    constructor(color: Colors, cell: Cell) {
        super(color, cell);
        this.logo = this.color === Colors.WHITE ? whiteLogo : blackLogo;
        this.figureName = FigureNames.KNIGHT;
    }

    public canMove(target: Cell): boolean {
        if (!super.canMove(target)) {
            return false;
        }
        if (
            (Math.abs(this.cell.x - target.x) === 2 && Math.abs(this.cell.y - target.y) === 1) ||
            (Math.abs(this.cell.x - target.x) === 1 && Math.abs(this.cell.x - target.x) === 2)
        ) {
            return true;
        }
        return false;
    }
}
