import { Colors, Nullable } from "../consts";
import { Board } from "./board";
import { Figure } from "./figure";

export class Cell {
    readonly x: number;
    readonly y: number;
    readonly color: Colors;
    currentFigure: Nullable<Figure>;
    isAvailable: boolean;
    id: number;
    board: Board;

    constructor(x: number, y: number, color: Colors, board: Board, figure: Nullable<Figure> = null) {
        this.x = x;
        this.y = y;
        this.color = color;
        this.currentFigure = figure;
        this.isAvailable = false;
        this.id = Math.random();
        this.board = board;
    }

    public moveFigure(target: Cell) {
        if (this.currentFigure) {
            target.currentFigure = this.currentFigure;
            this.currentFigure = null;
        }
    }

    public isEmpty() {
        return this.currentFigure === null;
    }

    public isEnemy(color: Colors) {
        return this.currentFigure?.color !== color;
    }
    public isEmptyHorizontal(target: Cell): boolean {
        if (this.y !== target.y) {
            return false;
        }
        const min = Math.min(this.x, target.x);
        const max = Math.max(this.x, target.x);
        for (let x = min + 1; x < max; x += 1) {
            if (!this.board.getCell(this.y, x).isEmpty()) {
                return false;
            }
        }
        return true;
    }

    public isEmptyVertical(target: Cell): boolean {
        if (this.x !== target.x) {
            return false;
        }
        const min = Math.min(this.y, target.y);
        const max = Math.max(this.y, target.y);
        for (let y = min + 1; y < max; y += 1) {
            if (!this.board.getCell(y, this.x).isEmpty()) {
                return false;
            }
        }
        return true;
    }

    public isEmptyDiagonal(target: Cell): boolean {
        const absX = Math.abs(this.x - target.x);
        const absY = Math.abs(this.y - target.y);
        if (absX !== absY) {
            return false;
        }
        const dirX = target.x > this.x ? 1 : -1;
        const dirY = target.y > this.y ? 1 : -1;
        for (let i = 1; i < absY; i += 1) {
            if (!this.board.getCell(this.y + i * dirY, this.x + i * dirX).isEmpty()) {
                return false;
            }
        }
        return true;
    }
}
