import React, { useEffect, useState } from 'react';

import './App.css';
import BoardComponent from './components/board/board';
import { Board } from './models/board';
import { Player } from './models/player';
import { Colors } from './models/colors';
import LostFigures from './components/lostFigures/lostFigures';
import Timer from './components/timer/timer';
import { Cell } from './models/cell';

function App() {
    const [board, setBoard] = useState(new Board());
    const [currentPlayer, setcurrentPlayer] = useState<Colors | null>(null);
    const [selectedCell, setSelectedCell] = useState<Cell | null>(null);

    useEffect(() => {
        restart();
    }, []);

    const restart = () => {
        const newBoard = new Board();
        newBoard.initCells();
        newBoard.addFigures();
        setBoard(newBoard);
        setSelectedCell(null);
        setcurrentPlayer(Colors.WHITE);

        console.clear();
    }

    const swapPlayer = () => {
        setcurrentPlayer(prev => {
            if (!prev) {
                return prev;
            }

            prev.color = prev.color === Colors.BLACK ? Colors.WHITE :  Colors.BLACK;
            return prev;
        })
    }

    useEffect(() => {
        console.log('CHANGED')
    }, [currentPlayer]);

    return (
        <div className="app">
            <Timer currentPlayer={currentPlayer} restart={restart}/>

                {board.isKingUnderAttack && 
                    <p>SHAH</p>
                }

            <BoardComponent
                board={board}
                selectedCell={selectedCell}
                setSelectedCell={setSelectedCell}
                setBoard={setBoard}
                currentPlayer={currentPlayer}
                swapPlayer={swapPlayer}
            />

            <div>
                <LostFigures
                    title="Black Figures"
                    figures={board.lostBlackFigures}
                />

                <LostFigures
                    title="White Figures"
                    figures={board.lostWhiteFigures}
                />
            </div>
        </div>
    );
}

export default App;
