import React, {useMemo, useState, Fragment, useCallback, memo, useEffect} from 'react';

import {Props} from './types';

import CellComponent from '../cell/cell';
import { Cell } from '../../models/cell';
import { Nullable } from '../../consts';

import './board.css';

const BoardComponent: Props = ({board, setBoard}) => {
    const [selectedCell, setSelectedCell] = useState<Nullable<Cell>>(null);

    const clickCellHandler = useCallback((cell: Cell) => {
        if (selectedCell?.x === cell.x && selectedCell.y === cell.y) {
            setSelectedCell(null);
            board.unlightenAvailableCells();
            return;
        }
        if (cell.currentFigure) {
            setSelectedCell(cell);
        }
        if (selectedCell?.currentFigure && cell.isAvailable) {
            selectedCell.currentFigure.move(cell);
            setBoard(board.updateBoard());
            setSelectedCell(null);
            board.unlightenAvailableCells();
        }
    }, [board, selectedCell]);

    const lightenCells = useCallback(() => {
        board.lightenAvailableCells(selectedCell);
        updateBoard();
    }, [selectedCell]);

    const updateBoard = () => {
        const newBoard = board.updateBoard();
        setBoard(newBoard);
    };

    useEffect(() => {
        if (selectedCell) {
            lightenCells();
        }
    }, [selectedCell]);

    return (
        <div className="board">
            {board.cells.map((row, index) => (
                <Fragment key={index}>
                    {row.map(cell => (
                        <CellComponent 
                            cell={cell}
                            isSelected={selectedCell?.x === cell.x && selectedCell.y === cell.y}
                            clickHandle={clickCellHandler}
                            key={cell.id}
                        />
                    ))}
                </Fragment>
            ))}
        </div>
    );
}

export default memo(BoardComponent);
