import React, { useEffect, useState } from 'react';

import './App.css';
import BoardComponent from './components/board/board';
import { Board } from './models/board';
import { Colors } from './models/colors';
import LostFigures from './components/lostFigures/lostFigures';
import Timer from './components/timer/timer';
import { Cell } from './models/cell';
import Info from './components/info/info';

function App() {
    const [board, setBoard] = useState(new Board());
    const [currentColor, setCurrentColor] = useState<Colors | null>(null);
    const [selectedCell, setSelectedCell] = useState<Cell | null>(null);

    useEffect(() => {
        restart();
    }, []);

    const restart = () => {
        const newBoard = new Board();
        newBoard.initCells();
        newBoard.addFigures();
        setBoard(newBoard);
        setSelectedCell(null);
        setCurrentColor(Colors.WHITE);

        console.clear();
    }

    const swapPlayer = () => {
        setCurrentColor(prev => {
            if (!prev) {
                return prev;
            }

            return prev === Colors.BLACK ? Colors.WHITE : Colors.BLACK;
        });
    }

    useEffect(() => {
        console.log('CHANGED');
    }, [currentColor]);

    return (
        <div className="app">
            {board.isKingUnderAttack && 
                <Info currentColor={currentColor}/>
            }

            <Timer currentColor={currentColor} restart={restart}/>

            <BoardComponent
                board={board}
                selectedCell={selectedCell}
                setSelectedCell={setSelectedCell}
                setBoard={setBoard}
                currentColor={currentColor}
                swapPlayer={swapPlayer}
            />

            <div>
                <LostFigures
                    title="Black Figures"
                    figures={board.lostBlackFigures}
                />

                <LostFigures
                    title="White Figures"
                    figures={board.lostWhiteFigures}
                />
            </div>
        </div>
    );
}

export default App;
