import { Colors, FigureNames } from "../consts";
import { Cell } from "./cell";
import logo from '../../assets/black-bishop.png'

export class Figure {
    logo: typeof logo | null;
    color: Colors
    cell: Cell;
    figureName?: FigureNames;
    id: number;
    isFirstMove: boolean;

    constructor(color: Colors, cell: Cell) {
        this.color = color;
        this.cell = cell;
        this.cell.currentFigure = this;
        this.logo = null;
        this.id = Math.random();
        this.isFirstMove = true;
    }

    public canMove(target: Cell): boolean {
        if (target.x === this.cell.x && target.y === this.cell.y) {
            return false;
        }
        if (target.currentFigure?.color === this.color) {
            return false;
        }
        if (target.currentFigure?.figureName === FigureNames.KING) {
            return false;
        }
        return true;
    }

    public move(target: Cell) {
        console.log('call');

        this.cell.currentFigure = null; 
        this.cell = target;
        this.cell.currentFigure = this;
        this.isFirstMove = false;
    }

    protected canMoveLines(target: Cell): boolean {
        return this.cell.isEmptyVertical(target) || this.cell.isEmptyHorizontal(target);
    }

    protected canMoveDiagonals(target: Cell): boolean {
        return this.cell.isEmptyDiagonal(target);
    }
}
