import React, { useEffect, useState } from 'react';

import './App.css';
import BoardComponent from './components/board/board';
import { Board } from './models/board';

const cn = 'app';

function App() {
  const [board, setBoard] = useState<Board>(new Board());

    useEffect(() => {
        getBoardInit();
    }, []);

    useEffect(() => {
        console.log('board changed');
        getBoardInit()
        // board.updateBoard();
    }, [board]);


    const getBoardInit = () => {
        const newBoard = new Board();
        newBoard.prepareCells();
        newBoard.addFigures();
        setBoard(newBoard);
    }

    return (
        <div className={cn}>
            <BoardComponent
                board={board}
                setBoard={setBoard}
            />
        </div>
    );
}

export default App;
