import { Cell } from "./cell";
import { Colors } from "./colors";

export class Board {
    cells: Cell[][] = [];

    constructor() {

    }

    public initCells() {
        for (let i = 0; i < 8; i += 1) {
            const row: Cell[] = [];

            for (let j = 0; j < 8; j += 1) {
                if ((i + j) % 2 === 0) {
                    row.push(new Cell(this, j, i, Colors.WHITE, null));
                } else {
                    row.push(new Cell(this, j, i, Colors.BLACK, null));
                }
            }

            this.cells.push(row);
        }
    }
}
