import {consts, Colors} from "../consts";
import { Cell } from "./cell";
import { Figure } from "./figure";
import { Bishop } from "./figures/bishop";
import { Knight } from "./figures/knight";
import { Pawn } from "./figures/pawn";
import { Queen } from "./figures/queen";
import { Rook } from "./figures/rook";

const {
    COLUMNS_AMOUNT, 
    ROWS_AMOUNT,
    POS_ROOK,
    POS_KNIGHT,
    POS_BISHOP,
    POS_QUEEN_WHITE,
    POS_QUEEN_BLACK,
    POS_KING_WHITE,
    POS_KING_BLACK,
} = consts;

export class Board {
    cells: Cell[][];

    constructor() {
        this.cells = [];
    }

    public prepareCells() {
        for (let i = 1; i <= COLUMNS_AMOUNT; i += 1) {
            const row: Cell[] = [];
            for (let j = 1; j <= ROWS_AMOUNT; j += 1) {
                if ((i + j) % 2 === 0) {
                    row.push(new Cell(j, i, Colors.WHITE));
                } else {
                    row.push(new Cell(j, i, Colors.BLACK));
                }
            }
            this.cells.push(row);
        }
    }

    private getCell(y: number, x: number) {
        return this.cells[y][x];
    }

    public addFigures() {
        this.addPawns();
        this.addRooks();
        this.addKnights();
        this.addBishops();
    }

    private add(figure: Figure, pos: number[]) {

    }

    private addPawns() {
        for (let i = 0; i < COLUMNS_AMOUNT; i += 1) {
            new Pawn(Colors.BLACK, this.getCell(1, i));
            new Pawn(Colors.WHITE, this.getCell(ROWS_AMOUNT - 2, i));
        }
    }

    private addRooks() {
        for (let i = 0; i < POS_ROOK.length; i += 1) {
            new Rook(Colors.BLACK, this.getCell(0, POS_ROOK[i]));
            new Rook(Colors.WHITE, this.getCell(ROWS_AMOUNT - 1, POS_ROOK[i]));
        }
    }

    private addKnights() {
        for (let i = 0; i < POS_KNIGHT.length; i += 1) {
            new Knight(Colors.BLACK, this.getCell(0, POS_KNIGHT[i]));
            new Knight(Colors.WHITE, this.getCell(ROWS_AMOUNT - 1, POS_KNIGHT[i]));
        }
    }

    private addBishops() {
        for (let i = 0; i < POS_BISHOP.length; i += 1) {
            new Bishop(Colors.BLACK, this.getCell(0, POS_BISHOP[i]));
            new Bishop(Colors.WHITE, this.getCell(ROWS_AMOUNT - 1, POS_BISHOP[i]));
        }
    }

    private addQueens() {
        new Queen(Colors.BLACK, this.getCell(0, 6));
        new Queen(Colors.WHITE, this.getCell(2, 6));
    }

    private addKings() {
        new Pawn(Colors.WHITE, this.getCell(2, 6));
    }
}
