import React, { useState } from 'react';

import {Props} from './types';

import './cell.css';

const cn = 'cell';

const CellComponent: Props = ({cell, isSelected}) => {
    const [selectedCell, setSelectedCell] = useState(false);

    const clickCellHandle = () => {
        setSelectedCell(prev => !prev);
    };

    return (
        <div className={[
            cn, 
            cell.color,
            cell.isAvailable && !!cell.currentFigure ? 'available-attack' : '',
            selectedCell ? 'selected' : '',
        ].join(' ')}
            onClick={clickCellHandle}
        >
            {cell.isAvailable && !cell.currentFigure && <div className={`${cn}-available`}/>}

            {cell.currentFigure && cell.currentFigure.logo &&
                <img
                    className="figure"
                    src={cell.currentFigure.logo} 
                    alt="figure"
                />
            }
        </div>
    );
}

export default CellComponent;
