import { Colors, Nullable } from "../consts";
import { Figure } from "./figure";

export class Cell {
    readonly x: number;
    readonly y: number;
    readonly color: Colors;
    currentFigure: Nullable<Figure>;
    isAvailable: boolean;
    id: number;

    constructor(x: number, y: number, color: Colors, figure: Nullable<Figure> = null) {
        this.x = x;
        this.y = y;
        this.color = color;
        this.currentFigure = figure;
        this.isAvailable = false;
        this.id = Math.random();
    }

    public moveFigure(target: Cell) {
        if (this.currentFigure) {
            target.currentFigure = this.currentFigure;
            this.currentFigure = null;
        }
    }

    private isEmpty() {

    }

    protected isEmptyHorizontal() {

    }

    protected isEmptyVertical() {

    }

    protected isEmptyDiagonal() {

    }
}
