import React, { useEffect, useMemo, useState, Fragment} from 'react';
import { Board } from '../../models/board';

import {Props} from './types';

import './board.css';
import CellComponent from '../cell/cell';
import { Cell } from '../../models/cell';

const cn = 'board';

const BoardComponent: Props = ({board, setBoard}) => {
    const [selectedCell, setSelectedCell] = useState<Cell | null>(null);

    const selectCellHandler = (cell: Cell) => {
        if (cell.currentFigure) {
            setSelectedCell(cell);
        }
    }

    const boardFinal = useMemo(() => {
        return (
            <>
                {board.cells.map((row, index) => (
                    <Fragment key={index}>
                        {row.map(cell => (
                            <div 
                                key={cell.id}
                                onClick={() => selectCellHandler(cell)}
                                className={`${cn}-cell`}
                            >
                                <CellComponent 
                                    cell={cell}
                                    isSelected={selectedCell?.x === cell.x && selectedCell.y === cell.y}
                                />
                            </div>
                        ))}
                    </Fragment>
                ))}
            </>
        )
    }, [board, selectedCell]);

    return (
        <div className={cn}>
            {boardFinal}
        </div>
    );
}

export default BoardComponent;
