import whiteLogo from '../../assets/white-pawn.png';
import blackLogo from '../../assets/black-pawn.png';

import { Colors, FigureNames } from "../../consts";
import { Cell } from "../cell";
import { Figure } from "../figure";

export class Pawn extends Figure {
    isFirstMove = false;

    constructor(color: Colors, cell: Cell) {
        super(color, cell);
        this.logo = this.color === Colors.WHITE ? whiteLogo : blackLogo;
        this.figureName = FigureNames.PAWN;
    }

    public canMove(target: Cell): boolean {
        if (!super.canMove(target)) {
            return false;
        }
        if (this.color === Colors.WHITE && this.canEat(this.cell.x, this.cell.y - 1, target, Colors.BLACK)) {
            return true;
        }
        if (this.color === Colors.BLACK && this.canEat(this.cell.x, this.cell.y + 1, target, Colors.WHITE)) {
            return true;
        }
        if (target.x !== this.cell.x) {            
            return false;
        }
        if (this.color === Colors.WHITE) {
            if (this.cell.y - target.y === 1) {
                return true;
            }
            if (this.isItsHalf(this.cell.y, this.color)) {
                return this.cell.y - target.y === 2;
            }
        }
        if (this.color === Colors.BLACK) {
            if (target.y - this.cell.y === 1) {
                return true;
            }
            if (this.isItsHalf(this.cell.y, this.color)) {
                return target.y - this.cell.y === 2;
            }
        }
        return false;
    }

    private isItsHalf(coordY: number, color: Colors): boolean {
        if (color === Colors.WHITE) {
            return coordY > 5;
        } else {
            return coordY < 2;
        }
    }

    private canEat(coordX: number, nextY: number, target: Cell, oppositeColor: Colors): boolean {       
        const result = target.y === nextY && 
            (target.x === coordX - 1 || target.x === coordX + 1) && 
            target.currentFigure?.color === oppositeColor;
        return result;
    }

    public canMove2(target: Cell) {
        const dir = this.color === Colors.BLACK ? 1 : -1;
        if (this.isFirstMove) {
            return Math.abs(target.x - this.cell.x);
        }
        if (target.x !== this.cell.x) {            
            return false;
        }
    }
}
