import {consts, Colors} from "../consts";
import { Cell } from "./cell";
import { Bishop } from "./figures/bishop";
import { King } from "./figures/king";
import { Knight } from "./figures/knight";
import { Pawn } from "./figures/pawn";
import { Queen } from "./figures/queen";
import { Rook } from "./figures/rook";

const {
    COLUMNS_AMOUNT, 
    ROWS_AMOUNT,
    POSES_ROOK,
    POSES_KNIGHT,
    POSES_BISHOP,
    POS_QUEEN_WHITE,
    POS_QUEEN_BLACK,
    POS_KING_WHITE,
    POS_KING_BLACK,
} = consts;

export class Board {
    cells: Cell[][];

    constructor() {
        this.cells = [];
    }

    public prepareCells() {
        for (let i = 0; i < COLUMNS_AMOUNT; i += 1) {
            const row: Cell[] = [];
            for (let j = 0; j < ROWS_AMOUNT; j += 1) {
                if ((i + j) % 2 === 0) {
                    const whiteCell = new Cell(j, i, Colors.WHITE);
                    whiteCell.board = this;
                    row.push(whiteCell);
                } else {
                    const blackCell = new Cell(j, i, Colors.WHITE);
                    blackCell.board = this;
                    row.push(blackCell);
                }
            }
            this.cells.push(row);
        }
    }

    public getCell(y: number, x: number) {
        return this.cells[y][x];
    }

    public addFigures() {
        this.addPawns();
        this.addRooks();
        this.addKnights();
        this.addBishops();
        this.addQueens();
        this.addKings();
    }

    private addPawns() {
        for (let i = 0; i < COLUMNS_AMOUNT; i += 1) {
            new Pawn(Colors.BLACK, this.getCell(1, i));
            new Pawn(Colors.WHITE, this.getCell(ROWS_AMOUNT - 2, i));
        }
    }

    private addRooks() {
        for (let i = 0; i < POSES_ROOK.length; i += 1) {
            new Rook(Colors.BLACK, this.getCell(0, POSES_ROOK[i]));
            new Rook(Colors.WHITE, this.getCell(ROWS_AMOUNT - 1, POSES_ROOK[i]));
        }
    }

    private addKnights() {
        for (let i = 0; i < POSES_KNIGHT.length; i += 1) {
            new Knight(Colors.BLACK, this.getCell(0, POSES_KNIGHT[i]));
            new Knight(Colors.WHITE, this.getCell(ROWS_AMOUNT - 1, POSES_KNIGHT[i]));
        }
    }

    private addBishops() {
        for (let i = 0; i < POSES_BISHOP.length; i += 1) {
            new Bishop(Colors.BLACK, this.getCell(0, POSES_BISHOP[i]));
            new Bishop(Colors.WHITE, this.getCell(ROWS_AMOUNT - 1, POSES_BISHOP[i]));
        }
    }

    private addQueens() {
        new Queen(Colors.BLACK, this.getCell(0, POS_QUEEN_BLACK));
        new Queen(Colors.WHITE, this.getCell(ROWS_AMOUNT - 1, POS_QUEEN_WHITE));
    }

    private addKings() {
        new King(Colors.BLACK, this.getCell(0, POS_KING_BLACK));
        new King(Colors.WHITE, this.getCell(ROWS_AMOUNT - 1, POS_KING_WHITE));
    }

    public updateBoard(): Board {
        const newBoard = new Board();
        newBoard.cells = this.cells;
        return newBoard;
    }

    public lightenAvailableCells(selectedCell: Cell) {
        for (let i = 0; i < ROWS_AMOUNT; i += 1) {
            for (let j = 0; j < COLUMNS_AMOUNT; j += 1) {
                const target = this.cells[i][j];
                target.isAvailable = !!selectedCell?.currentFigure?.canMove(target);
            }
        }
    }

    public unlightenAvailableCells() {
        for (let i = 0; i < ROWS_AMOUNT; i += 1) {
            for (let j = 0; j < COLUMNS_AMOUNT; j += 1) {
                this.cells[i][j].isAvailable = false;
            }
        }
    }
}
